#include "CAN/CanTypes.h"

#include "MessagePack.h"
#include "VEH_sigTx.c"


/******************************************************************************
 *                     P R I V A T E  F U N C T I O N S
 ******************************************************************************/

static bool pack_VEH_STW_switchStatus_10ms(CAN_data_T *message, const int counter)
{
    set_switch0Status(message, VEH, STW, switch0Status);
    set_switch1Status(message, VEH, STW, switch1Status);
    set_switch3Status(message, VEH, STW, switch3Status);
    set_switch4Status(message, VEH, STW, switch4Status);
    set_button0Status(message, VEH, STW, button0Status);
    set_button1Status(message, VEH, STW, button1Status);
    (void)counter;
    return true;
}

static const packTable_S BUS_A_packTable_10ms [] = {
    { &pack_VEH_STW_switchStatus_10ms, 0x600, 1U },
};
