/**
 * VEH_sigTx.h
 * Contains signal stuffing functions
 * Should eventually be autogenerated
 */

#pragma once

#include "CAN/CanTypes.h"
#include "lib_atomic.h"

// ________ ________ ________ ________ ________ ________ ________ _______u
static inline void set_VEH_STW_switch0Status(CAN_data_T* m, uint8_t val)
{
    // m->u8[0] ^= ((m->u8[0] ^ val << 0U) & 1U);
    atomicXorU16(&m->u16[0], ((m->u16[0] ^ val << 0U) & 1U));
}

