/**
 * VEH_sigTx.h
 * Contains signal stuffing functions
 * Should eventually be autogenerated
 */

#pragma once

#include "CAN/CanTypes.h"

// ________ ________ ________ ________ ________ ________ ________ _______u
static inline void set_VEH_STW_switch0Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 0U) & 1U);
}

// ________ ________ ________ ________ ________ ________ ________ ______u_
static inline void set_VEH_STW_switch1Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 1U) & 2U);
}

// ________ ________ ________ ________ ________ ________ ________ _____u__
static inline void set_VEH_STW_switch3Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 2U) & 4U);
}

// ________ ________ ________ ________ ________ ________ ________ ____u___
static inline void set_VEH_STW_switch4Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 3U) & 8U);
}

// ________ ________ ________ ________ ________ ________ ________ ___u____
static inline void set_VEH_STW_button0Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 4U) & 16U);
}

// ________ ________ ________ ________ ________ ________ ________ __u_____
static inline void set_VEH_STW_button1Status(CAN_data_T* m, uint8_t val)
{
    m->u8[0] ^= ((m->u8[0] ^ val << 5U) & 32U);
}
